	handleNewProjectInput(){
		return {

			setProjectName(event){
				const value = event.target.value.trim();
				// 判断是否为空
				if ( !(/^$/).test(value) ){
					this.setState({projectName:value});

					// 深度复制 避免覆盖 errors中其他对象
					let errors = Object.assign({},this.state.errors);
					errors.projectName = null;
					this.setState({
						errors:errors
					});
					return true;

				} else {
					// 深度复制
					let errors = Object.assign({},this.state.errors);
					errors.projectName = "projectname不能为空";
					this.setState({
						errors: errors,
						projectName: ""
					});
					return false;
				}
			},


			setGoal(event){
				const value = event.target.value.trim();
				// 判断是否全是数字
				if ( (/^\d+$/).test(value) ){
					this.setState({goal:value});

					let errors = Object.assign({},this.state.errors);
					errors.goal = null;
					this.setState({
						errors:errors
					});
					return true;

				} else {
					//深度复制
					let errors = Object.assign({},this.state.errors);
					errors.goal = "goal必须为数字";
					this.setState({
						errors: errors,
						goal: 0
					});
					return false;
				}
			},

			setMaxInvest(event){
				const value = event.target.value.trim();
				// 判断是否全是数字
				if ( (/^\d+$/).test(value) && value >= this.state.minInvest){

					let errors = Object.assign({},this.state.errors);
					errors.maxInvest = null;
					this.setState({
						errors:errors
					});
					return true;

				} else {
					//深度复制
					let errors = Object.assign({},this.state.errors);
					errors.maxInvest = "maxInvest必须为数字，必须大于或等于minInvest";
					this.setState({
						errors: errors,
						maxInvest: 0
					});
					return false;
				}
			},

			setMinInvest(event){
				const value = event.target.value.trim();
				// 判断是否全是数字
				if ( (/^\d+$/).test(value) && value >= this.state.maxInvest){

