import React from 'react';
import { Form, Button, Col } from 'react-bootstrap';

class newProject extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			owner: "",
			projectName: "",
			goal: 0,
			maxInvest: 0,
			minInvest: 0,
			description: "",
			symbol: "",
			name: "",
			decimals: 0,
			startDate: 0,
			bounsEnd: 0,
			bounsTokenToWei: 1,
			bonusTokenToWei: 0,
			endDate: 0,
			tokenToWei:0,
			errors :{

			}
		};

		this.handleNewProject = this.handleNewProjectInput()
	}

	handleNewProjectInput(){
		return {

			verifyProjectName(event){
				const value = event.target.value;
				// 判断是否为空
				if ( !(/^$/).test(value) ){
					this.setState({projectName:value});

					// 深度复制 避免覆盖 errors中其他对象
					let errors = Object.assign({},this.state.errors);
					errors.projectName = null;
					this.setState({
						errors:errors
					});
					return true;

				} else {
					// 深度复制
					let errors = Object.assign({},this.state.errors);
					errors.projectName = "projectname不能为空";
					this.setState({
						errors: errors,
						projectName: ""
					});
					return false;
				}
			},


			verifyGoal(event){
				const value = event.target.value;
				// 判断是否全是数字
				if ( (/^\d+$/).test(value) ){
					this.setState({goal:value});

					let errors = Object.assign({},this.state.errors);
					errors.goal = null;
					this.setState({
						errors:errors
					});
					return true;

				} else {
					//深度复制
					let errors = Object.assign({},this.state.errors);
					errors.goal = "goal必须为数字";
					this.setState({
						errors: errors,
						goal: 0
					});
					return false;
				}
			},

