					let errors = Object.assign({},this.state.errors);
					errors.minInvest = null;
					this.setState({
						errors:errors
					});
					return true;

				} else {
					//深度复制
					let errors = Object.assign({},this.state.errors);
					errors.maxInvest = "maxInvest必须为数字，必须大于或等于minInvest";
					this.setState({
						errors: errors,
						maxInvest: 0
					});
					return false;
				}
			},

