import React from 'react'
import web3,{nodeWeb3} from '../libs/web3';
import { Form, Alert, Button } from 'react-bootstrap';


class getETH extends React.Component{

	constructor(props){
		super(props);

		this.state = {
			res:"",
			error:""
		};
		window.nodeWeb3 = nodeWeb3;
	}

	async getETH(){
		const ethNum = this.refs.ethInput.value.trim();

		// 判断是否有metamask
		if (typeof window.web3 == 'undefined'){
			// 弹出错误，没有前端钱包
			this.setState({
				error:"没有发现前端钱包!"
			})
			return false;
		}
		// 获取账号 
		const accounts = await web3.eth.getAccounts();

		if (accounts.length === 0){
			// 弹出错误，提示请给本网站授权
			this.setState({
				error:"无法连接到前端钱包!"
			})
			return false;
		}

		if ( !(/^\d+&/.test(ethNum)) ){
			this.setState({
				error:"eth只能为数字"
			});
			return false;
		}
		const webAccount = await nodeWeb3.eth.getAccounts() [0];

		let num = await nodeWeb3.eth.getBalance(webAccount);

		this.setState({
			error:"",
			res:"获取eth成功！"
		})
	}

	render(){
		return (
			<Form>

				<Alert variant="success" show={this.state.res}>
					{this.state.res}
				</Alert>

				<Alert variant="danger" show={this.state.error}>
					{this.state.error}
				</Alert>

				<Form.Group controlId="formBasicEmail">
					<Form.Label>eth(非公网)</Form.Label>
					<Form.Control type="text" placeholder="eth num" ref="ethInput" />
					<Form.Text className="text-muted">
						eth为单位(1eth = 1,000,000,000,000,000,000wei)
					</Form.Text>
				</Form.Group>

				<Button variant="primary" type="button" onClick={this.getETH}>
					获取eth
				</Button>
			</Form>
		)
	}
}


export default getETH;
