import React from 'react';
import { Container,Row,Col } from 'react-bootstrap';
import web3 from '../libs/web3';
import getProjectContract from '../libs/project';

class Project extends React.Component{
	constructor(props){
		super(props);
		// 获取 路由参数address
		this.state = {
			project:{}
		}

	}

	async componentWillMount(){
		console.log(this.props)
		// 获取地址

		const address = this.props.match.params.address
		// 获取合约实例
		let instance = getProjectContract(address);

		console.log(instance);
		let [
			owner,
			projectName,
			goal,
			maxInvest,
			minInvest,
			description,
			symbol,
			name,
			decimals,
			startDate,
			bonusEnds,
			bonusTokenToWei,
			tokenToWei,
			endDate
		] = Object.values(await instance.methods.getDetails().call())
		let project = {
			owner,
			projectName: web3.utils.toUtf8(projectName),
			goal: web3.utils.fromWei(goal,"ether"),
			maxInvest,
			minInvest,
			description,
			symbol,
			name,
			decimals,
			startDate,
			bonusEnds,
			bonusTokenToWei,
			tokenToWei,
			endDate
		}
		this.setState({
			project: project
		})
	}
	render(){

		return (
			<div>
				<Container>
					<h1>当前项目详情</h1>
					<Row>
						<Col xs={{span:2}} className={"infoTitle"}>合约拥有者</Col>
						<Col>{this.state.project.owner}</Col>
						<Col xs={{span:2}} className={"infoTitle"}>项目名:</Col>
						<Col>{this.state.project.projectName}</Col>
					</Row>
					<Row>
						<Col xs={{span:2}} className={"infoTitle"}>合约拥有者</Col>
						<Col>{this.state.project.owner}</Col>
						<Col xs={{span:2}} className={"infoTitle"}>项目名:</Col>
						<Col>{this.state.project.projectName}</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default Project;
