import React from 'react';
import { Card, Button, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import web3 from  '../libs/web3';
import ProjectListContract from '../libs/projectList';
import getProjectContract from '../libs/project';

class ProjectList extends React.Component{

	constructor( props){
		super(props);
		this.state = {
			projects:[]
		}
		window.testWeb3 = web3
	}

	async componentWillMount(){
		// 获取所有众筹项目的地址
    	const addresses = await ProjectListContract.methods.getProjectList().call();
    	// 获取projectInfoList
    	const projectList = await Promise.all(addresses.map(address=> getProjectContract(address).methods.getGeneralInfo().call() ))

    	let projects = projectList.map( (obj,index)=>{

    		let [
				projectName,
				goal,
				maxInvest,
				minInvest,
				description,
				symbol,
				bonusEnds,
				balance
    		]=Object.values(obj);

    		return {
    			projectName: web3.utils.toUtf8(projectName),
				goal,
				maxInvest,
				minInvest,
				description,
				symbol: web3.utils.toUtf8(symbol),
				bonusEnds,
				address:addresses[index]
			}
    	} )

    	this.setState({
    		projects:projects
    	})
	}

	render(){
		let projectListInfo = this.state.projects.map( (project,index)=>{
			let flagBonusTime = project.bonusEnd > Date.parse(new Date())/1000;
			console.log(flagBonusTime,project.bonusEnd,Date.parse(new Date())/1000)
			return (
	    		<Card className={flagBonusTime ? "goldStyle" : "","mt20"} key={index}>
					<Card.Header> {project.projectName} </Card.Header>
					<Card.Body >
						<Card.Title className="textHidden">{project.description}</Card.Title>
						<Card.Text as={Row}>
							<Col>goal: <span className={"red"}>{web3.utils.fromWei(project.goal)}</span> eth</Col>
							<Col>maxInvest: <span className={"red"}>{web3.utils.fromWei(project.maxInvest)}</span> eth</Col>
							<Col>minInvest: <span className={"red"}>{web3.utils.fromWei(project.minInvest)}</span> eth</Col>
							<Col>完成度: <span className={"red"}>{web3.utils.fromWei(project.minInvest)}</span> eth</Col>
					    </Card.Text>
						<Button as={Link} className={"mt10"} to={"/projectDetail/"+project.address} variant={flagBonusTime?"warning":"success"} size="sm">Learn more</Button>
					</Card.Body>
				</Card>
			)
		})

		return (
			<div>
				<h1>众筹列表</h1>
				{ projectListInfo }
			</div>
		);
	}
}

export default ProjectList;
