import React from 'react';
import { Card, Button, Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import web3 from  '../libs/web3';
import ProjectListContract from '../libs/projectList';
import getProjectContract from '../libs/project';

class ProjectList extends React.Component{

	constructor( props){
		super(props);
		this.state = {
			projects:[]
		}
	}

	async componentWillMount(){
		// 获取所有众筹项目的地址
    	const addresses = await ProjectListContract.methods.getProjectList().call();
    	// 获取projectInfoList
    	const projectList = await Promise.all(addresses.map(address=> getProjectContract(address).methods.getContractInfo().call() ))

    	let projects = projectList.map( (obj,index)=>{
    		let [
    			owner,
				goal,
				maxInvest,
				minInvest,
				description,
				symbol,
				name,
				decimals,
				_totalSupply,
				startDate,
				bonusEnds,
				bonusTokenToWei,
				tokenToWei,
				endDate
    		]=Object.values(obj);

    		return { 
    			owner,
				goal,
				maxInvest,
				minInvest,
				description,
				symbol,
				name,
				decimals,
				_totalSupply,
				startDate,
				bonusEnds,
				bonusTokenToWei,
				tokenToWei,
				endDate,
				address: addresses[index] 
			}
    	} )

    	this.setState({
    		projects:projects
    	})
	}

	render(){
		let projectListInfo = this.state.projects.map( (project,index)=>{
			let flagBonusTime = project.bonusEnd < Date.parse(new Date()) ? "goldStyle" : "bronzeStyle";
			return (
	    		<Card className={ flagBonusTime }>
					<Card.Header>{project.projectName}</Card.Header>
					<Card.Body as={Container}>
						<Card.Title className="textHidden">{project.description}</Card.Title>
						<Row>
							<Col>goal: {web3.utils.fromWei(project.goal)}</Col>
							<Col>maxInvest: {web3.utils.fromWei(project.maxInvest)}</Col>
							<Col>minInvest: {web3.utils.fromWei(project.minInvest)}</Col>
						</Row>
						<Button as={Link} to={"/projectDetail/"+project.address} tovariant="primary">Learn more</Button>
					</Card.Body>
				</Card>
			)
		})

		return (
			<div>
				<projectListInfo />
			</div>
		);
	}
}

export default ProjectList;
