import React from 'react';
import { Container,Row,Col } from 'react-bootstrap';
import web3 from '../libs/web3';
import getProjectContract from '../libs/project';

class Project extends React.Component{
	constructor(props){
		super(props);
		// 获取 路由参数address
		this.state = {
			project:{}
		}

	}

	async componentWillMount(){
		console.log(this.props)
		// 获取地址

		const address = this.props.match.params.address
		// 获取合约实例
		let instance = getProjectContract(address);

		console.log(instance);
		let [
			owner,
			projectName,
			goal,
			maxInvest,
			minInvest,
			description,
			symbol,
			name,
			decimals,
			startDate,
			bonusEnds,
			bonusTokenToWei,
			tokenToWei,
			endDate
		] = Object.values(await instance.methods.getDetails().call())
		let project = {
			owner,
			projectName: web3.utils.toUtf8(projectName),
			goal: web3.utils.fromWei(goal,"ether"),
			maxInvest: web3.utils.fromWei(maxInvest,"ether"),
			minInvest: web3.utils.fromWei(minInvest,"ether"),
			description,
			symbol: web3.utils.toUtf8(symbol),
			name: web3.utils.toUtf8(name),
			decimals,
			startDate: new Date(startDate),
			bonusEnds: new Date(bonusEnds),
			bonusTokenToWei: web3.utils.fromWei(bonusTokenToWei,"ether"),
			tokenToWei: web3.utils.fromWei(tokenToWei,"ether"),
			endDate: new Date(endDate)
		}
		this.setState({
			project: project
		})
	}

	render(){

		return (
			<div>
				<Table striped bordered hover>
					<thead>
						<tr>
						<th>#</th>
						<th>First Name</th>
						<th>Last Name</th>
						<th>Username</th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<td>1</td>
							<td>Mark</td>
							<td>Otto</td>
							<td>@mdo</td>
						</tr>
						<tr>
							<td>2</td>
							<td>Jacob</td>
							<td>Thornton</td>
							<td>@fat</td>
						</tr>
						<tr>
							<td>3</td>
							<td colSpan="2">Larry the Bird</td>
							<td>@twitter</td>
						</tr>
					</tbody>
				</Table>
			</div>
		);
	}
}

export default Project;
